var menuArray=[];$("li").each(function(){menuArray.push(Math.round($(this).outerWidth(!0)))});var menuPlacement=function(a){for(var e=$("li.active").index(),n=0,i=0;e>=i;i++){if(!(e>i)){if(i===e){n+=Math.round(menuArray[i]/2);break}break}n+=menuArray[i]}$("nav ul").css("margin-left",-n)};menuPlacement();var activePane=function(a){var e=a+1;$(".panel").removeClass("active"),$("#content-container > div:nth-child("+e+")").addClass("active")};$("nav li").click(function(){$("nav li").removeClass("active"),$(this).addClass("active");var a=$(this).index();menuPlacement(a),activePane(a)});